      setLoadingPerformance(false);
      return;
    }

    const token = localStorage.getItem("token");
    if (!token) {
      setPerformanceError("Not authenticated");
      setLoadingPerformance(false);
      return;
    }

    try {
      const res = await axios.get(`/api/admins/performance/${employeeIDForPerformance}`, {
